class day1Test {
    fun parseFile(name: String): List<Int> {
        val fileUri = javaClass.classLoader.getResource(name).toURI()
        return File(fileUri).readLines()[0].split(" ").map { s -> s.toInt() }
    }

    @Test
    fun test1() {
        assertEquals(0, day1("(())"))
        assertEquals(0, day1("()()"))
    }

    @Test
    fun test2() {
        assertEquals(3, day1("((("))
        assertEquals(3, day1("(()(()("))
        assertEquals(3, day1("))((((("))
    }

    @Test
    fun test3() {
        assertEquals(-1, day1("())"))
        assertEquals(-1, day1("))("))
    }

    @Test
    fun test4() {
        assertEquals(-3, day1(")))"))
        assertEquals(-3, day1(")())())"))
    }

